import './Background.css'
import "animejs/lib/anime.min"

function Background() {

    let textWrapper = document.querySelector('.gradient');
    textWrapper.innerHTML = textWrapper.textContent.replace(/\S/g, "<span class='letter'>$&</span>");
    anime.timeline({ loop: true })
        .add({
            targets: '.gradient .letter',
            opacity: [0, 1],
            easing: "easeInOutQuad",
            duration: 2250,
            delay: (el, i) => 150 * (i + 1)
        }).add({
            targets: '.gradient',
            opacity: 0,
            duration: 1000,
            easing: "easeOutExpo",
            delay: 1000
        })
    //JSX como o react le e tranforma elementos no DOM
    return (
        <div className="background">
            <div id='it’smorethanjustatrip' className='gradient'>
                It’s more than just a trip</div>

            <img src="/images/aircanada.jpeg" alt="main backgroud image" />
        </div>
    )
}
export default Background
